import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './BrowseOpportunities.css';

const BrowseOpportunities = ({ opportunities }) => {
  const [applied, setApplied] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  // üîê Check if user is logged in
  useEffect(() => {
    const checkAuth = async () => {
      try {
        await axios.get("http://127.0.0.1:5000/dashboard", {
          withCredentials: true,
        });
        // User is authenticated ‚Äî do nothing
      } catch (err) {
        // Not authenticated ‚Äî redirect to login
        navigate("/login");
      }
    };

    checkAuth();
  }, [navigate]);

  const handleApply = (id) => {
    if (!applied.includes(id)) {
      setApplied([...applied, id]);
      alert("You have successfully applied!");
    }
  };

  const checkDashboardAccess = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:5000/dashboard", {
        withCredentials: true,
      });
      alert("‚úÖ Access granted: " + res.data.message);
    } catch (err) {
      alert("‚ùå Access denied: " + (err.response?.status || err.message));
    }
  };

  const filteredOpportunities = opportunities.filter((opp) =>
    opp.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    opp.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
    opp.location.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="browse-container">
      <h2 className="browse-title">Browse Opportunities</h2>

      <button onClick={checkDashboardAccess} className="check-dashboard-button">
        üîê Check Dashboard Access
      </button>

      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search opportunities..."
        className="search-input"
      />

      <ul className="opportunities-list">
        {filteredOpportunities.map((opp) => (
          <li key={opp.id} className="opportunity-item">
            <h3 className="opportunity-title">{opp.title}</h3>
            <p className="opportunity-description">{opp.description}</p>
            <p className="opportunity-location">
              <strong>Location:</strong> {opp.location}
            </p>
            <button
              onClick={() => handleApply(opp.id)}
              disabled={applied.includes(opp.id)}
              className={`apply-button ${applied.includes(opp.id) ? "applied" : ""}`}
            >
              {applied.includes(opp.id) ? "Applied" : "Apply"}
            </button>
          </li>
        ))}
        {filteredOpportunities.length === 0 && (
          <p className="no-results">No opportunities found.</p>
        )}
      </ul>
    </div>
  );
};

export default BrowseOpportunities;
